export interface ISides {
    top: string;
    right: string;
    bottom: string;
    left: string;
    important?: boolean;
}
/**
 * Parses CSS sides (e.g., the value of `margin`, `padding` and `border` declarations).
 */
export default function parseSides(
/**
 * The CSS sides to parse (e.g., `0 5% 10px !important`).
 */
value: string): ISides;
